import React, {useEffect} from 'react';
import s from './Users.module.css'
import User from "./User/User";
import OneUserWindow from "./OneUserWindow/OneUserWindow";
import {getUsers} from "../redux/users-reducer";
import {connect} from "react-redux";

const Users = (props) => {
    debugger
    let users = props.users;
    //let currentPage = props.currentPage;
    const [currentPage, setCurrentPage] = useEffect(1)

    if (!users) {
        users = props.getUsers();
    }

    let totalCount = users.length + 1;
    let pageCount = Math.ceil(totalCount / 5);
    let pages = [];

    const onChangeCurrentPage = (page) => {
        setCurrentPage(page)
    };


    for (let page = 1; page < pageCount + 1; page++) {
        pages.push(page)
    }

    /*    if (users === 'error') {
            return 'Server connection error'
        }*/

    return (
        <div className={s.users}>
            <OneUserWindow users={users}/>
            <div>{
                pages.map((page) => {
                    return (
                        <div>{(page === currentPage) ?
                            <button className={s.activePageButton}>{page}</button>
                            : <button className={s.pageButton}
                                      onClick={(e) => {onChangeCurrentPage(page)}}>{page}</button>
                        }</div>
                    )
                })
            }

            </div>
            <User users={users} currentPage={props.currentPage}/>
        </div>
    )
};

const mapStateToProps = (state) => ({
    users: state.usersPage.users,
    getUsers: state.usersPage.getUsers,
   // currentPage: state.usersPage.currentPage
});

export default connect(mapStateToProps, {getUsers})(Users)
