const SET_USERS = 'users-reducer/SET_USERS';
const SET_CURRENT_PAGE = 'users-reducer/SET_CURRENT_PAGE'

const initialState = {
    users: [],
    pageSize: 5,
    currentPage: 1
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USERS:
            return ({
                ...state,
                users: action.users
            });
        case SET_CURRENT_PAGE:
            return ({
                ...state,
                currentPage: action.page
            });
        default:
            return state
    }
};

export const getUsers = () => async(dispatch) => {
    let response = await fetch('https://github.com/SerhiiOsadchyi/test2-react-toweriqua/blob/master/users.json');
    alert (response)
};

export default usersReducer
